console.clear();

function slide1__init(){
  function slide1(){
    //복사할 아이템 선택
    var itemFirst = $('.slide1 > .slide-wrap > li:first-child');
    
    $('.slide1 > .slide-wrap').animate({
      left: -1200 + 'px'
    }, 500, function(){
      //첫번째 아이템 복사
      $('.slide1 > .slide-wrap').append(itemFirst.clone());
      //기존 첫번째 아이템 삭제
      itemFirst.remove();
      //위치 초기화
      $('.slide1 > .slide-wrap').css('left', 0);
    })
  }
  
  setInterval(function(){
    slide1()
  }, 2500);
}

function slide2__init(){
  function slide2(){
    var itemFirst = $('.slide2 > .slide-wrap > li:first-child');
    
    // 탑부분만 바뀌었습니다
    $('.slide2 > .slide-wrap').animate({
      top: -300 + 'px'
    }, 500, function(){
      $('.slide2 > .slide-wrap').append(itemFirst.clone());
      itemFirst.remove();
      $('.slide2 > .slide-wrap').css('top', 0);
    })
  }
  
  setInterval(function(){
    slide2()
  }, 2500);
}

function slide3__init(){
  var slideIndex = 0;
  function slide3(){
    if(slideIndex == 2){
      slideIndex = 0;
    }else {
      ++slideIndex;
    }
    $('.slide3 > .slide-wrap > li').eq(slideIndex).fadeIn(500);
    $('.slide3 > .slide-wrap > li').eq(slideIndex - 1).fadeOut(500);
  }
  setInterval(function(){
    slide3()
  }, 2500)
}




$(function(){
  slide1__init();
  slide2__init();
  slide3__init();
})