mapper.select:
SELECT * FROM ${tablename} 
WHERE LOWER(storeAddress) LIKE '%' || LOWER(#{storeAddress}) || '%' 
AND LOWER(storeName) LIKE '%' || LOWER(#{storeName}) || '%'

select.foreach:
SELECT * FROM ${tableName}
WHERE LOWER(StoreName) LIKE LOWER('%' || #{searchString} || '%') 
   OR LOWER(StoreAddress) LIKE LOWER('%' || #{searchString} || '%')
